import { useEffect, useState } from "react";
import { useForm } from "react-hook-form";
import { useNavigate } from "react-router-dom";
import MeetingModel from "../../../Models/MeetingModel";
import TeamModel from "../../../Models/TeamModel";
import meetingsService from "../../../Services/MeetingsService";

import "./AddMeeting.css";

function AddMeeting(): JSX.Element {

    const navigate = useNavigate();
    const [teams, setTeams] = useState<TeamModel[]>([]);
    const { register, handleSubmit, setValue } = useForm<MeetingModel>();

    // دالة لتحويل تاريخ API إلى صيغة مناسبة لحقل datetime-local
    function formatDateForInput(dateString: string): string {
        const date = new Date(dateString);
        return date.toISOString().slice(0,16);
    }

    useEffect(() => {
        meetingsService.getAllTeams()
            .then(teams => setTeams(teams))
            .catch(err => alert("Failed to load teams: " + err.message));
    }, []);

    async function send(meeting: MeetingModel) {
        if (new Date(meeting.startTime).getTime() > new Date(meeting.endTime).getTime()) {
            alert("End time cannot be before start time.");
            return;
        }

        try {
            await meetingsService.addMeeting(meeting);
            alert("Meeting has been added successfully.");
            navigate("/list");
        }
        catch (err: any) {
            // تحقق من وجود حالة تعارض
            if (err.response?.status === 409 && err.response.data?.suggestion) {
                const suggestion = err.response.data.suggestion;
                alert(`Conflict detected! Suggested Time: ${suggestion.time}, Room: ${suggestion.room}`);

                // تعبئة الفورم بالقيم المقترحة
                setValue("startTime", formatDateForInput(suggestion.time));
                // لو عندك endTime في الاقتراح استخدمه، وإلا خلي مدة الاجتماع ساعة مثلاً
                if (suggestion.endTime) {
                    setValue("endTime", formatDateForInput(suggestion.endTime));
                } else {
                    // افتراضياً أضف ساعة على الوقت المقترح
                    const end = new Date(suggestion.time);
                    end.setHours(end.getHours() + 1);
                    setValue("endTime", end.toISOString().slice(0,16));
                }
                setValue("room", suggestion.room);
                // لو حابب تعيد اختيار الفريق مثلاً:
                if (suggestion.teamId) setValue("teamId", suggestion.teamId);
            }
            else {
                alert("Failed to add meeting: " + (err.message || err));
            }
        }
    }

    return (
        <div className="AddMeeting">
            <h2>Add Meeting</h2>

            <form onSubmit={handleSubmit(send)}>

                <label>Select Team:</label>
                <select defaultValue="" {...register("teamId")} required>
                    <option disabled value="">-- Select Team --</option>
                    {teams.map(t => (
                        <option key={t.id} value={t.id}>{t.name}</option>
                    ))}
                </select>

                <label>Start of Meeting:</label>
                <input type="datetime-local" {...register("startTime")} required />

                <label>End of Meeting:</label>
                <input type="datetime-local" {...register("endTime")} required />

                <label>Description:</label>
                <input type="text" {...register("description")} required minLength={1} maxLength={100} />

                <label>Meeting Room:</label>
                <input type="text" {...register("room")} required minLength={1} maxLength={100} />

                <button type="submit">Add</button>
            </form>
        </div>
    );
}

export default AddMeeting;
