import { useEffect, useState } from "react";
import { useForm } from "react-hook-form";
import { useLocation, useNavigate } from "react-router-dom";
import MeetingModel, { Rooms } from "../../../Models/MeetingModel";
import TeamModel from "../../../Models/TeamModel";
import meetingsService from "../../../Services/MeetingsService";

import "./AddMeeting.css";

function AddMeeting(): JSX.Element {
    const navigate = useNavigate();
    const location = useLocation();

    const suggestion = location.state as {
        startTime?: string;
        endTime?: string;
        room?: string;
        teamId?: number;
    } | null;

    const [teams, setTeams] = useState<TeamModel[]>([]);
    const [conflictSuggestion, setConflictSuggestion] = useState<null | {
        startTime?: string;
        endTime?: string;
        room?: string;
    }>(null);

    const { register, handleSubmit, setValue } = useForm<MeetingModel>();

    useEffect(() => {
        meetingsService.getAllTeams()
            .then(setTeams)
            .catch(err => console.error("Failed to load teams:", err.message));
    }, []);

    useEffect(() => {
        if (suggestion) {
            if (suggestion.teamId) setValue("teamId", suggestion.teamId);
            if (suggestion.startTime) setValue("startTime", suggestion.startTime);
            if (suggestion.endTime) setValue("endTime", suggestion.endTime);
            if (suggestion.room) setValue("room", suggestion.room as Rooms);
        }
    }, [suggestion, setValue]);

    async function send(meeting: MeetingModel) {
        setConflictSuggestion(null);

        if (new Date(meeting.startTime).getTime() >= new Date(meeting.endTime).getTime()) {
            setConflictSuggestion({
                startTime: "Start time must be before end time",
                endTime: "",
                room: ""
            });
            return;
        }

        try {
            await meetingsService.addMeeting(meeting);
            navigate("/list");
        } catch (err: any) {
            if (err.response?.status === 409 && err.response.data) {
                setConflictSuggestion(err.response.data);
            } else {
                setConflictSuggestion({
                    startTime: "Failed to add meeting",
                    endTime: "",
                    room: ""
                });
                console.error("Error:", err.message);
            }
        }
    }

    return (
        <div className="AddMeeting">
            <h2>Add Meeting</h2>

            {conflictSuggestion && (
                <div style={{ border: "1px solid red", padding: "10px", marginBottom: "10px" }}>
                    <h3>Conflict detected!</h3>
                    <p>
                        Suggested Start:{" "}
                        {conflictSuggestion.startTime && !conflictSuggestion.startTime.startsWith("Failed")
                            ? new Date(conflictSuggestion.startTime).toLocaleString()
                            : conflictSuggestion.startTime || "Unavailable"}
                    </p>
                    <p>
                        Suggested End:{" "}
                        {conflictSuggestion.endTime
                            ? new Date(conflictSuggestion.endTime).toLocaleString()
                            : "Unavailable"}
                    </p>
                    <p>Suggested Room: {conflictSuggestion.room || "Unavailable"}</p>
                </div>
            )}

            <form onSubmit={handleSubmit(send)}>

                <label>Select Team:</label>
                <select {...register("teamId", { valueAsNumber: true })} required>
                    <option disabled value="">-- Select Team --</option>
                    {teams.map(t => (
                        <option key={t.id} value={t.id}>{t.name}</option>
                    ))}
                </select>

                <label>Start of Meeting:</label>
                <input type="datetime-local" {...register("startTime")} required />

                <label>End of Meeting:</label>
                <input type="datetime-local" {...register("endTime")} required />

                <label>Description:</label>
                <input type="text" {...register("description")} required minLength={1} maxLength={100} />

                <label>Meeting Room:</label>
                <select {...register("room")} required>
                    <option disabled value="">-- Select Room --</option>
                    {Object.values(Rooms).map(room => (
                        <option key={room} value={room}>{room}</option>
                    ))}
                </select>

                <button type="submit">Add</button>
            </form>
        </div>
    );
}

export default AddMeeting;
