import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import meetingsService from "../../../Services/MeetingsService";
import MeetingModel, { Rooms } from "../../../Models/MeetingModel";

function AddMeeting(): JSX.Element {
  const [conflictSuggestion, setConflictSuggestion] = useState<null | {
    startTime: string;
    endTime: string;
    room: string;
  }>(null);

  const [errorMessage, setErrorMessage] = useState<string | null>(null);

  const navigate = useNavigate();

  async function send(meeting: MeetingModel) {
    setConflictSuggestion(null);
    setErrorMessage(null);

    if (new Date(meeting.startTime) >= new Date(meeting.endTime)) {
      setErrorMessage("End time must be after start time.");
      return;
    }

    try {
      await meetingsService.addMeeting(meeting);
      alert("Meeting added successfully");
      navigate("/list");
    } catch (err: any) {
      if (err.response?.status === 409 && err.response?.data?.suggestion) {
        // تعارض وجدنا اقتراح
        setConflictSuggestion(err.response.data.suggestion);
      } else {
        setErrorMessage("Failed to add meeting: " + (err.message ?? "Unknown error"));
      }
    }
  }

  return (
    <div>
      <h2>Add Meeting</h2>

      {errorMessage && <p style={{ color: "red" }}>{errorMessage}</p>}

      {conflictSuggestion && (
        <div style={{ border: "1px solid red", padding: "10px", marginBottom: "10px" }}>
          <h3>Conflict detected!</h3>
          <p>Suggested start: {new Date(conflictSuggestion.startTime).toLocaleString()}</p>
          <p>Suggested end: {new Date(conflictSuggestion.endTime).toLocaleString()}</p>
          <p>Suggested room: {conflictSuggestion.room}</p>
        </div>
      )}

      {/* هنا نموذج الإدخال مع زر الإرسال send */}
      {/* لفهم أكثر كيف تستخدم send يمكنك أن تعطيني كود الإدخال عندك */}
    </div>
  );
}

export default AddMeeting;
