import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import meetingsService from "../../../src/Services/MeetingsService";

function SuggestMeeting(): JSX.Element {
    const [suggestion, setSuggestion] = useState<{ time: string; room: string } | null>(null);
    const navigate = useNavigate();
    const teamId = 1; // عدّل هذا حسب ID الفريق المناسب في تطبيقك

    useEffect(() => {
        async function fetchSuggestion() {
            try {
                const result = await meetingsService.suggestMeeting(teamId);
                setSuggestion(result);
            } catch (err) {
                console.error("Failed to fetch suggestion", err);
            }
        }
        fetchSuggestion();
    }, [teamId]);

    return (
        <div className="SuggestMeeting">
            {suggestion ? (
                <>
                    <h3>Suggested Meeting</h3>
                    <p><strong>Time:</strong> {suggestion.time}</p>
                    <p><strong>Room:</strong> {suggestion.room}</p>
                </>
            ) : (
                <p>No meeting suggestion available.</p>
            )}

            <button onClick={() => navigate("/insert")}>Add a New Meeting</button>
        </div>
    );
}

export default SuggestMeeting;
